sys_ui_script.script:
class ReactorDemo extends React.Component
{
	constructor(props)
	{
		super(props);
	}

	onButtonClicked()
	{
		alert('ReactorButton: Button has been clicked');
	}

	render()
	{
		var aComponents = [];
		aComponents.push(e(Clock, { key: 'clock' },null));
		aComponents.push(e(Button,{ key: 'button', text: 'A Button', callbackOnClick: this.onButtonClicked },null));
		return e('div',null,aComponents);
	}

}
